import os
from flask import Blueprint, send_from_directory, render_template, request

from .models import NodeRegistration

frontend_bp = Blueprint('frontent', __name__)

dir_path = os.path.dirname(os.path.realpath(__file__))


@frontend_bp.route('/static/<path:path>')
def send_js(path):
    return send_from_directory(dir_path, os.path.join('static', path))


@frontend_bp.route('/', methods=['GET'])
def capacity():
    countries = NodeRegistration.all_countries()
    nodes = []
    form = {
        'mru': 0,
        'cru': 0,
        'sru': 0,
        'hru': 0,
        'country': '',
    }

    if len(request.args) != 0:
        mru = request.args.get('mru') or None
        if mru:
            form['mru'] = int(mru)
        cru = request.args.get('cru') or None
        if cru:
            form['cru'] = int(cru)
        sru = request.args.get('sru') or None
        if sru:
            form['sru'] = int(sru)
        hru = request.args.get('hru') or None
        if hru:
            form['hru'] = int(hru)
        form['country'] = request.args.get('country') or ''

        nodes = list(NodeRegistration.search(**form))

    return render_template('capacity.html', nodes=nodes, form=form, countries=countries)


@frontend_bp.route('/farmers', methods=['GET'])
def farmers():
    return render_template('farmers.html')
